window.addEventListener('load', function () {
  loadJSON();
});

function loadJSON() {
  $.getJSON('posts.json', function (e) {
    $('.tag-wrapper').append(`${e.tagTitle}`);
    $('.tag-date').append(`${e.tagDate}`);
    $('.heading-wrapper').append(`${e.mainTitle}`);
    $('.article-main-image').attr('src', `img/${e.mainImage}`);
    $('.featured-product-item-wrapper img').attr(
      'src',
      `img/${e.relatedImage}`,
    );
    $('.featured-product-item-wrapper h2').append(`${e.relatedTitle}`);
    $('.featured-product-item-wrapper p').append(`${e.relatedContent}`);
    $('.featured-product-item-wrapper span').append(`${e.relatedCoupon}`);
    for (f = 0; f < e.authors.length; f++) {
      $('.article-author-content-wrapper').append(`
        <div class="author-name-wrapper-2">
          <img loading="lazy" alt="" src="img/${e.authors[f].image}" class="author-medical-image">
          <div class="heading-medical-wrapper">
            <span class="medical-review-heading">${e.authors[f].content}</span>
            <h2 class="author-heading">${e.authors[f].title}</h2>
          </div>
        </div>
        `);
    }

    for (f = 0; f < e.posts.length; f++) {
      if (e.posts[f].image != '') {
        $('.article-left-content-wrapper').append(
          `<img src="img/${e.posts[f].image}" loading="lazy" alt="">`,
        );
      }
      if (e.posts[f].title != '') {
        $('.article-left-content-wrapper').append(
          `<h4>${e.posts[f].title}</h4>`,
        );
      }
      if (e.posts[f].content != '') {
        jQuery.each(e.posts[f].content, function (q, e) {
          $('.article-left-content-wrapper').append(`<p>${e}</p>`);
        });
      }
      if (e.posts[f].coupon != '') {
        $('.article-left-content-wrapper').append(
          `<span>${e.posts[f].coupon}</span>`,
        );
      }
    }
  });
}
